import React from "react";
import { MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";
import Axios from 'axios'
class FormComponent extends React.Component {
  state = {
   email:"",
   password:"",
   ConfrimPassword:"",
  };

  submitHandler = event => {
    event.preventDefault();
    const {password, ConfrimPassword,confirmed}=this.state;
    if(password===ConfrimPassword){
        event.target.className += " was-validated"
        Axios.post('https://react-app-9a030.firebaseio.com/test.json', {
            userId: '12345667',
            profilePicture: 'https://profilepicture.com/url',
            username: 'test',
        }
    )
    .then(function(response) {
        console.log(response);
    })
    .catch(function(error) {
        console.log(error);
    });

    }else{
        console.log("not matched")
    }

  };
 
  changeHandler = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    return (
      <div className="container" style={{marginTop:20,position:"fixed",top:"50%",left:"50%",  transform: "translate(-15%, -80%)", }}>
        <form
          className="needs-validation"
          onSubmit={this.submitHandler}
          noValidate
        >
            <h3 style={{lineHeight:4}}>Sign UP Form</h3>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.email}
                onChange={this.changeHandler}
                type="email"
                name="email"
                label="email"
                required
              >
                
              </MDBInput>
            </MDBCol>
            
          </MDBRow>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.password}
                onChange={this.changeHandler}
                type="password"
                name="password"
                label="password"
                required
              >
                
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.ConfrimPassword}
                onChange={this.changeHandler}
                type="password"
                name="ConfrimPassword"
                label="password"
                required
              >
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBBtn color="success" type="submit">
            Sign Up
          </MDBBtn>
        </form>
      </div>
    );
  }
}

export default FormComponent;
