import React from "react";
import { MDBRow, MDBCol, MDBInput, MDBBtn } from "mdbreact";

class FormComponent extends React.Component {
  state = {
   email:"",
   password:"",
   ConfrimPassword:""
  };

  submitHandler = event => {
    event.preventDefault();
    const {password, ConfrimPassword}=this.state;
    password===ConfrimPassword ? console.log(this.state) : null;
    event.target.className += " was-validated"

  };

  changeHandler = event => {
    this.setState({ [event.target.name]: event.target.value });
  };

  render() {
    return (
      <div className="container" style={{marginTop:20,position:"fixed",top:"50%",left:"50%",  transform: "translate(-15%, -80%)", }}>
        <form
          className="needs-validation"
          onSubmit={this.submitHandler}
          noValidate
        >
            <h3 style={{lineHeight:4}}>Sign UP Form</h3>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.email}
                onChange={this.changeHandler}
                type="email"
                name="email"
                label="email"
                required
              >
                <div className="invalid-feedback"></div>
              </MDBInput>
            </MDBCol>
            
          </MDBRow>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.password}
                onChange={this.changeHandler}
                type="password"
                name="password"
                label="password"
                required
              >
                <div className="invalid-feedback">
                </div>
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBRow>
            <MDBCol md="4">
              <MDBInput
                value={this.state.ConfrimPassword}
                onChange={this.changeHandler}
                type="password"
                name="ConfrimPassword"
                label="password"
                required
              >
                <div className="invalid-feedback">
                </div>
              </MDBInput>
            </MDBCol>
          </MDBRow>
          <MDBBtn color="success" type="submit">
            Sign Up
          </MDBBtn>
        </form>
      </div>
    );
  }
}

export default FormComponent;
